--
-- Name: followup_stat; Type: VIEW; Schema: public; Owner: openpg
--

CREATE VIEW public.followup_stat AS
 SELECT l.id,
    l.partner_id,
    min(l.date) AS date_move,
    max(l.date) AS date_move_last,
    max(l.followup_date) AS date_followup,
    max(l.followup_line_id) AS followup_id,
    sum(l.debit) AS debit,
    sum(l.credit) AS credit,
    sum((l.debit - l.credit)) AS balance,
    l.company_id,
    l.blocked
   FROM (public.account_move_line l
     LEFT JOIN public.account_account a ON ((l.account_id = a.id)))
  WHERE (((a.account_type)::text = 'asset_receivable'::text) AND (l.full_reconcile_id IS NULL) AND (l.partner_id IS NOT NULL))
  GROUP BY l.id, l.partner_id, l.company_id, l.blocked;


ALTER TABLE public.followup_stat OWNER TO openpg;

--
-- Name: followup_stat_by_partner; Type: VIEW; Schema: public; Owner: openpg
--

CREATE VIEW public.followup_stat_by_partner AS
 SELECT ((l.partner_id * (10000)::bigint) + l.company_id) AS id,
    l.partner_id,
    min(l.date) AS date_move,
    max(l.date) AS date_move_last,
    max(l.followup_date) AS date_followup,
    max(l.followup_line_id) AS max_followup_id,
    sum((l.debit - l.credit)) AS balance,
    l.company_id
   FROM (public.account_move_line l
     LEFT JOIN public.account_account a ON ((l.account_id = a.id)))
  WHERE (((a.account_type)::text = 'asset_receivable'::text) AND (l.full_reconcile_id IS NULL) AND (l.partner_id IS NOT NULL))
  GROUP BY l.partner_id, l.company_id;


