-- Name: report_pos_order; Type: VIEW; Schema: public; Owner: openpg
--

CREATE VIEW public.report_pos_order AS
 SELECT min(l.id) AS id,
    count(*) AS nbr_lines,
    s.date_order AS date,
    sum(l.qty) AS product_qty,
    sum(((l.qty * l.price_unit) /
        CASE COALESCE(s.currency_rate, (0)::numeric)
            WHEN 0 THEN 1.0
            ELSE s.currency_rate
        END)) AS price_sub_total,
    sum(round(((((l.qty * l.price_unit) * ((100)::numeric - l.discount)) / (100)::numeric) /
        CASE COALESCE(s.currency_rate, (0)::numeric)
            WHEN 0 THEN 1.0
            ELSE s.currency_rate
        END), cu.decimal_places)) AS price_total,
    sum((((l.qty * l.price_unit) * (l.discount / (100)::numeric)) /
        CASE COALESCE(s.currency_rate, (0)::numeric)
            WHEN 0 THEN 1.0
            ELSE s.currency_rate
        END)) AS total_discount,
        CASE
            WHEN (sum((l.qty * u.factor)) = (0)::numeric) THEN NULL::numeric
            ELSE (sum(((l.qty * l.price_unit) /
            CASE COALESCE(s.currency_rate, (0)::numeric)
                WHEN 0 THEN 1.0
                ELSE s.currency_rate
            END)) / sum((l.qty * u.factor)))
        END AS average_price,
    sum((to_char((date_trunc('day'::text, s.date_order) - date_trunc('day'::text, s.create_date)), 'DD'::text))::integer) AS delay_validation,
    s.id AS order_id,
    s.partner_id,
    s.state,
    s.user_id,
    s.company_id,
    s.sale_journal AS journal_id,
    l.product_id,
    pt.categ_id AS product_categ_id,
    p.product_tmpl_id,
    ps.config_id,
    pt.pos_categ_id,
    s.pricelist_id,
    s.session_id,
    (s.account_move IS NOT NULL) AS invoiced,
    sum((l.price_subtotal - (l.total_cost /
        CASE COALESCE(s.currency_rate, (0)::numeric)
            WHEN 0 THEN 1.0
            ELSE s.currency_rate
        END))) AS margin,
    s.employee_id
   FROM (((((((public.pos_order_line l
     JOIN public.pos_order s ON ((s.id = l.order_id)))
     LEFT JOIN public.product_product p ON ((l.product_id = p.id)))
     LEFT JOIN public.product_template pt ON ((p.product_tmpl_id = pt.id)))
     LEFT JOIN public.uom_uom u ON ((u.id = pt.uom_id)))
     LEFT JOIN public.pos_session ps ON ((s.session_id = ps.id)))
     LEFT JOIN public.res_company co ON ((s.company_id = co.id)))
     LEFT JOIN public.res_currency cu ON ((co.currency_id = cu.id)))
  GROUP BY s.id, s.date_order, s.partner_id, s.state, pt.categ_id, s.user_id, s.company_id, s.sale_journal, s.pricelist_id, s.account_move, s.create_date, s.session_id, l.product_id, pt.pos_categ_id, p.product_tmpl_id, ps.config_id, s.employee_id;


