CREATE VIEW public.report_project_task_user AS
 SELECT ( SELECT 1) AS nbr,
    t.id,
    t.id AS task_id,
    t.active,
    t.create_date,
    t.date_assign,
    t.date_end,
    t.date_last_stage_update,
    t.date_deadline,
    t.project_id,
    t.priority,
    t.name,
    t.company_id,
    t.partner_id,
    t.parent_id,
    t.ancestor_id,
    t.stage_id,
    t.is_closed,
    t.kanban_state AS state,
    t.milestone_id,
    pm.is_reached AS milestone_reached,
    pm.deadline AS milestone_deadline,
    NULLIF(t.rating_last_value, (0)::double precision) AS rating_last_value,
    avg(rt.rating) AS rating_avg,
    t.working_days_close,
    t.working_days_open,
    t.working_hours_open,
    t.working_hours_close,
    (date_part('epoch'::text, ((t.date_deadline)::timestamp without time zone - timezone('UTC'::text, now()))) / ((3600 * 24))::double precision) AS delay_endings_days,
    t.sale_line_id,
    t.sale_order_id
   FROM ((public.project_task t
     LEFT JOIN public.rating_rating rt ON (((rt.res_id = t.id) AND ((rt.res_model)::text = 'project.task'::text) AND (rt.consumed = true) AND (rt.rating >= (1)::double precision))))
     LEFT JOIN public.project_milestone pm ON ((pm.id = t.milestone_id)))
  WHERE (t.project_id IS NOT NULL)
  GROUP BY t.id, t.active, t.create_date, t.date_assign, t.date_end, t.date_last_stage_update, t.date_deadline, t.project_id, t.ancestor_id, t.priority, t.name, t.company_id, t.partner_id, t.parent_id, t.stage_id, t.is_closed, t.kanban_state, t.rating_last_value, t.working_days_close, t.working_days_open, t.working_hours_open, t.working_hours_close, t.milestone_id, pm.is_reached, pm.deadline, t.sale_line_id, t.sale_order_id;


