--
-- Name: vendor_delay_report _RETURN; Type: RULE; Schema: public; Owner: openpg
--

CREATE OR REPLACE VIEW public.vendor_delay_report AS
 SELECT m.id,
    m.date,
    m.purchase_line_id,
    m.product_id,
    min(pc.id) AS category_id,
    min(po.partner_id) AS partner_id,
    sum(pol.product_uom_qty) AS qty_total,
    sum(
        CASE
            WHEN ((pol.date_planned)::date >= (m.date)::date) THEN ml.qty_done
            ELSE (0)::numeric
        END) AS qty_on_time
   FROM ((((((public.stock_move m
     JOIN public.stock_move_line ml ON ((m.id = ml.move_id)))
     JOIN public.purchase_order_line pol ON ((pol.id = m.purchase_line_id)))
     JOIN public.purchase_order po ON ((po.id = pol.order_id)))
     JOIN public.product_product p ON ((p.id = m.product_id)))
     JOIN public.product_template pt ON ((pt.id = p.product_tmpl_id)))
     JOIN public.product_category pc ON ((pc.id = pt.categ_id)))
  WHERE ((m.state)::text = 'done'::text)
  GROUP BY m.id;


--
-- Name: im_livechat_report_channel _RETURN; Type: RULE; Schema: public; Owner: openpg
--

CREATE OR REPLACE VIEW public.im_livechat_report_channel AS
 SELECT c.id,
    c.uuid,
    c.id AS channel_id,
    c.name AS channel_name,
    concat(l.name, ' / ', c.id) AS technical_name,
    c.livechat_channel_id,
    c.create_date AS start_date,
    to_char(date_trunc('hour'::text, c.create_date), 'YYYY-MM-DD HH24:MI:SS'::text) AS start_date_hour,
    to_char(date_trunc('hour'::text, c.create_date), 'HH24'::text) AS start_hour,
    date_part('dow'::text, c.create_date) AS day_number,
    date_part('epoch'::text, (max(m.create_date) - min(m.create_date))) AS duration,
    date_part('epoch'::text, (min(mo.create_date) - min(m.create_date))) AS time_to_answer,
    count(DISTINCT c.livechat_operator_id) AS nbr_speaker,
    count(DISTINCT m.id) AS nbr_message,
        CASE
            WHEN (EXISTS ( SELECT DISTINCT m_1.author_id
               FROM public.mail_message m_1
              WHERE ((m_1.author_id = c.livechat_operator_id) AND (m_1.res_id = c.id) AND ((m_1.model)::text = 'mail.channel'::text) AND (c.livechat_operator_id = m_1.author_id)))) THEN 0
            ELSE 1
        END AS is_without_answer,
    (date_part('day'::text, (date_trunc('day'::text, now()) - (date_trunc('day'::text, c.create_date))::timestamp with time zone)) + (1)::double precision) AS days_of_activity,
        CASE
            WHEN (c.anonymous_name IS NULL) THEN 0
            ELSE 1
        END AS is_anonymous,
    c.country_id,
        CASE
            WHEN (rate.rating = (5)::double precision) THEN 1
            ELSE 0
        END AS is_happy,
    rate.rating,
        CASE
            WHEN (rate.rating = (1)::double precision) THEN 'Unhappy'::text
            WHEN (rate.rating = (5)::double precision) THEN 'Happy'::text
            WHEN (rate.rating = (3)::double precision) THEN 'Neutral'::text
            ELSE NULL::text
        END AS rating_text,
        CASE
            WHEN (rate.rating > (0)::double precision) THEN 0
            ELSE 1
        END AS is_unrated,
    c.livechat_operator_id AS partner_id
   FROM ((((public.mail_channel c
     JOIN public.mail_message m ON (((m.res_id = c.id) AND ((m.model)::text = 'mail.channel'::text))))
     JOIN public.im_livechat_channel l ON ((l.id = c.livechat_channel_id)))
     LEFT JOIN public.mail_message mo ON (((m.res_id = c.id) AND ((m.model)::text = 'mail.channel'::text) AND (mo.author_id = c.livechat_operator_id))))
     LEFT JOIN public.rating_rating rate ON (((rate.res_id = c.id) AND ((rate.res_model)::text = 'mail.channel'::text) AND ((rate.parent_res_model)::text = 'im_livechat.channel'::text))))
  WHERE (c.livechat_operator_id IS NOT NULL)
  GROUP BY c.livechat_operator_id, c.id, c.name, c.livechat_channel_id, l.name, c.create_date, c.uuid, rate.rating;


--
-- Name: im_livechat_report_operator _RETURN; Type: RULE; Schema: public; Owner: openpg
--

CREATE OR REPLACE VIEW public.im_livechat_report_operator AS
 SELECT row_number() OVER () AS id,
    c.livechat_operator_id AS partner_id,
    c.livechat_channel_id,
    count(DISTINCT c.id) AS nbr_channel,
    c.id AS channel_id,
    c.create_date AS start_date,
    date_part('epoch'::text, (max(m.create_date) - min(m.create_date))) AS duration,
    date_part('epoch'::text, (min(mo.create_date) - min(m.create_date))) AS time_to_answer
   FROM ((public.mail_channel c
     JOIN public.mail_message m ON (((m.res_id = c.id) AND ((m.model)::text = 'mail.channel'::text))))
     LEFT JOIN public.mail_message mo ON (((mo.res_id = c.id) AND ((m.model)::text = 'mail.channel'::text) AND (mo.author_id = c.livechat_operator_id))))
  WHERE (c.livechat_channel_id IS NOT NULL)
  GROUP BY c.id, c.livechat_operator_id;


